let dropdownText = document.getElementsByClassName("dropdown")[0];
ascendingElement.addEventListener("click", function () {
  customerSection.innerHTML = "";
  document.getElementById("searchInput").value = "";
  renderCustomerData(allCustomers.sort(sortAscFn));
});

descendingElement.addEventListener("click", function () {
  customerSection.innerHTML = "";
  document.getElementById("searchInput").value = "";
  renderCustomerData(allCustomers.sort(sortDscFn));
});
